var http = require('http');
http.createServer(function (req, res) {
  res.writeHead(200, {'Content-Type': 'text/plain'});
  res.end('Hello World from Cloud9\n');
}).listen(process.env.PORT);
var request = require('request');
var fs = require('fs');

request('http://visualizedata.github.io/datastructures/', function (error, response, body) {
  if (!error && response.statusCode == 200) {
    fs.writeFileSync('/home/ubuntu/workspace/data/syllabus.txt', body);
  }
  else {console.error('request failed')}
})

request('http://nyintergroup.org/meetinglist/', function (error, response, body) {
  if (!error && response.statusCode == 200) {
    fs.writeFileSync('meetinglist.cfm?zone=02&borough=M', body);
  }
  else {console.error('request failed')}
})